import React from 'react';
import { useOdontoStates } from "../Context/Context";
import Card from "../Components/Card";

const Favs = () => {
  const { favs } = useOdontoStates();

  return (
    <>
      <h1>Dentistas Destacados</h1>
      <div className="card-grid">
        {favs.map(fav => (
          <Card
            odontologo={fav}
            key={fav.id}
            removeFromFavs={removeFromFavs} // Pasar la función removeFromFavs para eliminar de Destacados
          />
        ))}
      </div>
    </>
  );
};

export default Favs;
