import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Detail = () => {
  const [lista, setLista] = useState({});
  const params = useParams();

  const url = `https://jsonplaceholder.typicode.com/users/` + params.id;

  useEffect(() => {
    let isMounted = true; // Variable para rastrear si el componente está montado

    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        if (isMounted) {
          // Verifica si el componente aún está montado antes de actualizar el estado
          setLista(data);
        }
      });

    return () => {
      // Función de limpieza que se ejecuta cuando el componente se desmonta
      isMounted = false;
    };
  }, [url]); // Agrega url al array de dependencias

  return (
    <>
<h1>Detalle del dentista</h1>

<table className="card" style={{ margin: '20' }}>
  <tbody>
    <tr>
      <th>Nombre:</th>
      <td>{lista.name}</td>
    </tr>
    <tr>
      <th>Email:</th>
      <td>{lista.email}</td>
    </tr>
    <tr>
      <th>Phone:</th>
      <td>{lista.phone}</td>
    </tr>
    <tr>
      <th>Website:</th>
      <td>{lista.website}</td>
    </tr>
  </tbody>
</table>

    </>
  );
};

export default Detail;
