import React from 'react';
import { Link } from 'react-router-dom';
import { useOdontoStates } from '../Context/Context';

const Card = ({ odontologo, removeFromFavs }) => {
  const { favs } = useOdontoStates();

  const isFav = favs.some((fav) => fav.id === odontologo.id);

  const handleClickRemoveFromFavs = () => {
    if (typeof removeFromFavs === 'function') {
      removeFromFavs(odontologo);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center' }} className="card">
        <Link to={`/recipe/` + odontologo.id}>
          <img src="/images/doctor.jpg" alt="Card" width={200} />
          <p>ID: {odontologo.id}</p>
          <p>Nombre y apellido: {odontologo.name}</p>
        </Link>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        {isFav ? (
          <button onClick={handleClickRemoveFromFavs} className="favButton">‚ùå</button>
        ) : null}
      </div>
    </div>
  );
};

export default Card;
