import React from 'react';
import { useOdontoStates } from '../Context/Context';
import Card from '../Components/Card';

const Home = () => {
  const { odontologos, favs, setFavs } = useOdontoStates();

  const isFav = (odontologo) => {
    return favs.some((fav) => fav.id === odontologo.id);
  };

  const addToFavs = (odontologoToAdd) => {
    if (!isFav(odontologoToAdd)) {
      setFavs([...favs, odontologoToAdd]);
    }
  };

  return (
    <main className="">
      <h1>Home</h1>
      <div className='card-grid'>
        {odontologos.map((odontologo) => (
          <Card
            odontologo={odontologo}
            key={odontologo.id}
            addToFavs={addToFavs}
            isFav={isFav(odontologo)} // Comprueba si es un favorito
          />
        ))}
      </div>
    </main>
  );
};

export default Home;
