import React from 'react';
import { Link } from 'react-router-dom';

const Card = ({ odontologo, addToFavs }) => {
  // Verifica que addToFavs sea una función antes de usarla
  const handleClick = () => {
    if (typeof addToFavs === 'function') {
      addToFavs(odontologo);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center' }} className="card">
        <Link to={`/recipe/${odontologo.id}`}>
        <img src="/images/doctor.jpg" alt="Card" width={200} />
          <p>id: {odontologo.id}</p>
          <p>Nombre y apellido: {odontologo.name}</p>
          <p>nombre: {odontologo.username}</p>
        </Link>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button onClick={handleClick} className="favButton">⭐</button>
      </div>
    </div>
  );
};

export default Card;





