import React, { useState, useEffect } from 'react';
import { useParams } from 'react-router-dom';

const Detail = () => {
  const [lista, setLista] = useState({});
  const params = useParams();

  const url = `https://jsonplaceholder.typicode.com/users/` + params.id;

  useEffect(() => {
    let isMounted = true; // Variable para rastrear si el componente está montado

    fetch(url)
      .then((res) => {
        return res.json();
      })
      .then((data) => {
        if (isMounted) {
          // Verifica si el componente aún está montado antes de actualizar el estado
          setLista(data);
        }
      });

    return () => {
      // Función de limpieza que se ejecuta cuando el componente se desmonta
      isMounted = false;
    };
  }, [url]); // Agrega url al array de dependencias

  return (
    <>
      <h1>Detalle del dentista</h1>

      <div style={{ margin: '20' }} className="card-grid">
        <h2> nombre: {lista.name}</h2>
        <img src="/images/doctor.jpg" alt="Card" width={200} />
        <h4> email: {lista.email}</h4>
        <h4> phone: {lista.phone}</h4>
        <h4> website: {lista.website}</h4>
      </div>
    </>
  );
};

export default Detail;
