import React from 'react';
import { Link } from 'react-router-dom';

const Card = ({ odontologo, removeFromFavs, addToFavs }) => {
  const handleClickAddToFavs = () => {
    if (typeof addToFavs === 'function') {
      addToFavs(odontologo);
    }
  };

  const isFav = removeFromFavs || addToFavs;

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center' }} className="card">
        <Link to={`/recipe/` + odontologo.id}>
          <img src="/images/doctor.jpg" alt="Card" width={200} />
          <p>ID: {odontologo.id}</p>
          <p>Nombre y apellido: {odontologo.name}</p>
        </Link>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        {isFav && !removeFromFavs ? (
          <button onClick={handleClickAddToFavs} className="favButton">⭐</button>
        ) : null}
        {removeFromFavs ? (
          <button onClick={() => removeFromFavs(odontologo)} className="favButton">❌</button>
        ) : null}
      </div>
    </div>
  );
};

export default Card;
