import React, { useState } from "react";
import '../index.css';

const Form = () => {
  const [newUser, setNewUser] = useState({
    message: '',
    email: ''
  });

  const [error, setError] = useState(null);
  const [success, setSuccess] = useState(null);

  const handleChange = (e) => {
    setNewUser({
      ...newUser,
      [e.target.name]: e.target.value
    });
  };

  const handleSubmit = (e) => {
    e.preventDefault();

    // Validaciones
    if (newUser.message.length <= 5 || !/^\S+@\S+\.\S+$/.test(newUser.email)) {
      setError("Por favor verifique su información nuevamente");
      setSuccess(null);
      return;
    }

    // Si pasa las validaciones, muestra el mensaje de éxito
    setSuccess(`Gracias ${newUser.message}, te contactaremos cuando antes vía mail`);
    setError(null);

    // Limpia los campos del formulario
    setNewUser({
      message: '',
      email: ''
    });
  };

  return (
    <div>
      <form onSubmit={handleSubmit} className="my-form">
        <label>
          Nombre y apellido
          <input type="text" name="message" onChange={handleChange} value={newUser.message} />
        </label>
        <label>
          Email
          <input type="email" name="email" onChange={handleChange} value={newUser.email} />
        </label>
        <button>Submit</button>
      </form>
      {error && <p className="error-message">{error}</p>}
      {success && <p className="success">{success}</p>}
    </div>
  );
};

export default Form;
