import { Link } from 'react-router-dom';
import { useOdontoStates } from '../Context/Context';

const Card = ({ odontologo }) => {
  const { favs, setFavs } = useOdontoStates();

  // Función para agregar un odontólogo a la lista de favoritos si no está en ella
  const addToFavs = (odontologo) => {
    // Verifica si el odontólogo ya está en la lista de favoritos
    if (!favs.some((fav) => fav.id === odontologo.id)) {
      // Si no está en la lista, agrégalo
      setFavs([...favs, odontologo]);
    }
  };

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center' }} className="card">
        <Link to={`/recipe/` + odontologo.id}>
          <img src="/images/doctor.jpg" alt="Card" width={200} />
          <p>ID: {odontologo.id}</p>
          <p>Nombre y apellido: {odontologo.name}</p>
        </Link>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        <button onClick={() => addToFavs(odontologo)} className="favButton">⭐</button>
      </div>
    </div>
  );
};

export default Card;
