import React from 'react';
import { useTheme } from '../Context/ThemeContext'; // Importa el contexto del tema
// Importa los íconos de las redes sociales
import FacebookIcon from '../images/ico-facebook.png';
/* import InstagramIcon from '../images/instagram.png';
import TiktokIcon from '../images/twitter.png';
import WhatsaapIcon from '../images/instagram.png'; */

const Footer = () => {
  const { theme } = useTheme(); // Obtiene el tema actual desde el contexto del tema

  return (
    <footer className={theme === 'dark' ? 'dark' : 'light'}>
      <p>Copyright 2023 - Del Castillo, Luis Javier.</p>
      <img src="../images/DH.png" alt="DH-logo" />
      {/* Agrega los íconos de las redes sociales */}
      <div>
        <a href="URL_DE_TU_PERFIL_DE_FACEBOOK" target="_blank" rel="noopener noreferrer">
          <img src={FacebookIcon} alt="Facebook" />
        </a>

      </div>
    </footer>
  );
}

export default Footer;
