import { Link } from 'react-router-dom';
import { useOdontoStates } from '../Context/Context';

const Card = ({ odontologo, addToFavs, removeFromFavs }) => {
  const { favs } = useOdontoStates();

  // Función para agregar un odontólogo a la lista de favoritos si no está en ella
  const handleClickAddToFavs = () => {
    if (typeof addToFavs === 'function') {
      addToFavs(odontologo);
    }
  };

  // Función para eliminar un odontólogo de la lista de favoritos
  const handleClickRemoveFromFavs = () => {
    if (typeof removeFromFavs === 'function') {
      removeFromFavs(odontologo);
    }
  };

  const isFav = favs.some((fav) => fav.id === odontologo.id);

  return (
    <div>
      <div style={{ display: 'flex', justifyContent: 'center' }} className="card">
        <Link to={`/recipe/` + odontologo.id}>
          <img src="/images/doctor.jpg" alt="Card" width={200} />
          <p>ID: {odontologo.id}</p>
          <p>Nombre y apellido: {odontologo.name}</p>
        </Link>
      </div>
      <div style={{ display: 'flex', justifyContent: 'center' }}>
        {isFav ? (
          <button onClick={handleClickRemoveFromFavs} className="favButton">❌</button>
        ) : (
          <button onClick={handleClickAddToFavs} className="favButton">⭐</button>
        )}
      </div>
    </div>
  );
};

export default Card;
