import React from 'react';
import { useOdontoStates } from "../Context/Context";
import Card from '../Components/Card';

// Este componente deberá ser estilado como "dark" o "light" dependiendo del theme del Context

const Home = () => {
  const { odontologos, favs, setFavs } = useOdontoStates(); // Obtén también la lista de favoritos y la función para actualizarla

  // Función para eliminar un odontólogo de la lista de favoritos desde Home
  const removeFromFavs = (odontologoToRemove) => {
    const updatedFavs = favs.filter((fav) => fav.id !== odontologoToRemove.id);
    setFavs(updatedFavs);
  };

  return (
    <main className="" >
      <h1>Home</h1>
      <div className='card-grid'>
        {odontologos.map(odontologo => (
          <Card
            odontologo={odontologo}
            key={odontologo.id}
            removeFromFavs={removeFromFavs} // Pasa la función removeFromFavs como prop al componente Card
          />
        ))}
      </div>
    </main>
  );
}

export default Home;
