import React from "react";
import { useOdontoStates } from "../Context/Context";
import Card from "../Components/Card";

const Favs = () => {
  const { favs } = useOdontoStates();

  // Función para agregar un odontólogo a la lista de favoritos si no está en ella
  const addToFavs = (odontologo) => {
    // Verifica si el odontólogo ya está en la lista de favoritos
    if (!favs.some((fav) => fav.id === odontologo.id)) {
      // Si no está en la lista, agrégalo
      // Puedes implementar la lógica para agregarlo aquí
    }
  };

  return (
    <>
      <h1>Dentistas Destacados</h1>
      <div className="card-grid">
        {favs.map((fav) => (
          <Card
            odontologo={fav}
            key={fav.id}
            // Pasa la función addToFavs como prop al componente Card
            addToFavs={addToFavs}
          />
        ))}
      </div>
    </>
  );
};

export default Favs;
